import React, { useState } from 'react'
import { MinuteMilliseconds } from '../Constants'
import { ProjectData } from "../DoData"

const datestampToMinute = (): number => {
    const now = new Date()

    const nowYear = now.getFullYear()
    const nowMonthIndex = now.getMonth()
    const nowMonthDay = now.getDate()
    const nowHour = now.getHours()
    const nowMinute = now.getMinutes()
    const nowTimeZoneOffset = now.getTimezoneOffset() * MinuteMilliseconds
    const dayStart = Date.UTC(nowYear, nowMonthIndex, nowMonthDay, nowHour, nowMinute) + nowTimeZoneOffset
    return dayStart
}

interface ProjectEditProps {
    project: ProjectData
    save: (project: ProjectData) => void
}

const ProjectEdit = ({ project, save }: ProjectEditProps) => {
    const [text, setText] = useState(project.text || '')
    const beginning = project.beginning || datestampToMinute()

    return <div >
        <label>text:<input
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setText(data)
            }}
            value={text}
        /></label>
        <br />
        <label>beginning:<input readOnly value={new Date(beginning).toISOString()} /></label>
        <button onClick={() => save({ text: text, beginning: beginning })} >Done</button>

    </div>
}

export default ProjectEdit
