import React, { useState } from 'react'
import { MinuteMilliseconds } from '../Constants'
import { Event } from "../DoData"
import Entry from './Entry'

interface EventEditProps {
    event: Event
    save: (event: Event) => void
}

const EventEdit = ({ event, save }: EventEditProps) => {
    const [text, setText] = useState(event.text || '')
    const now = new Date()
    const [year, setYear] = useState(now.getFullYear())
    const [month, setMonth] = useState(now.getMonth() + 1)
    const [date, setDate] = useState(now.getDate())
    const [hours, setHours] = useState(now.getHours() - 1)
    // console.log(`++now minutes: ${now.getMinutes()}`)
    const [minutes, setMinutes] = useState(now.getMinutes())
    // console.log(`++minutes: ${minutes}`)
    // console.log(`now minutes: ${now.getMinutes()}`)
    const [duration, setDuration] = useState(30)
    const timeZoneOffset = now.getTimezoneOffset() 
    console.log(`timezone offset: ${timeZoneOffset}`)

    if (minutes >= 60) setMinutes(minutes / 10)

    return <div >
        <label>text:<input
            value={text}
            size={40}
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setText(data)
            }}
        /></label>
        <br />
        <Entry prompt={'date:'} value={year} size={4} setter={setYear} />
        <Entry prompt={'-'} value={month} size={2} setter={setMonth} />
        <Entry prompt={'-'} value={date} size={2} setter={setDate} />
        <br />
        <Entry prompt={'time:'} value={hours} size={2} setter={setHours} />
        <Entry prompt={':'} value={minutes} size={2} setter={setMinutes} />
        <br />
        <Entry prompt={'duration:'} value={30} size={2} setter={setDuration} />
        <br />
        <button onClick={() => {
            console.log(`minutes: ${minutes}`)
            const dateString = `${year}`
                + `-${String(month).padStart(2, '0')}`
                + `-${String(date).padStart(2, '0')}`
                + `T${String(hours).padStart(2, '0')}`
                + `:${String(minutes).padStart(2, '0')}:00.000`
            console.log(`Date string: ${dateString}`)
            const start = Date.parse(dateString)
            console.log(`Start date: ${new Date(start).toISOString()}`)
            save(
                { text: text, start: start, duration: duration, }
            )
        }
        } >Done</button>

    </div>
}

export default EventEdit
