import React, { useState } from 'react'
import { Event } from "../DoData"

interface EventEditProps {
    event: Event
    save: (event: Event) => void
}

const EventEdit = ({ event, save }: EventEditProps) => {
    const [text, setText] = useState(event.text || '')
    const now = new Date()

    return <div >
        <label>text:<input
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setText(data)
            }}
            value={text}
        /></label>
        <br />
        <label>date:<input  value={now.getFullYear()} /></label>
        <label>-<input value={now.getMonth() + 1}  /></label>
        <label>-<input  value={now.getDay()} /></label>
        <br />
        <label>time:<input   /></label>
        <label>:<input   /></label>
        <br />
        <label>duration:<input   /></label>
        <br />
        <button onClick={() =>
            save(
                { text: text, start: 0, duration: 0, }
            )
        } >Done</button>

    </div>
}

export default EventEdit
