import React, { useState } from 'react'
import { Event } from "../DoData"

interface EventEditProps {
    event: Event
    save: (event: Event) => void
}

const EventEdit = ({ event, save }: EventEditProps) => {
    const [text, setText] = useState(event.text || '')
    const now = new Date()
    const [year, setYear] = useState(now.getFullYear())
    const [month, setMonth] = useState(now.getMonth())

    return <div >
        <label>text:<input
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setText(data)
            }}
            value={text}
        /></label>
        <br />
        <label>date:<input 
        defaultValue={year} 
        maxLength={4}
        size={4}
        onInput={event => {
            const data = (event.target as HTMLInputElement).value
            setYear(parseInt(data))
        }}
        /></label>
        <label>-<input defaultValue={month + 1} /></label>
        <label>-<input defaultValue={now.getDate()} /></label>
        <br />
        <label>time:<input /></label>
        <label>:<input /></label>
        <br />
        <label>duration:<input defaultValue={'30'} /></label>
        <br />
        <button onClick={() => {
            const start=Date.parse(`${year}-${month}`)
            console.log(`Start date: ${new Date(start).toISOString()}`)
            save(
                { text: text, start: start, duration: 0, }
            )
        }
        } >Done</button>

    </div>
}

export default EventEdit
