import { ChangeEvent, FormEvent } from "react"
import { ToDo } from "./DoData"

const fileHandler = async (event: FormEvent<HTMLInputElement>, setData: (data: ToDo[]) => void) => {
    console.log("event: ", event)
    const target = (event.target as HTMLInputElement)
    console.log("target files: ", target.files)
    console.log("blob: ", target.files?.item(0)?.text())

    const blobData = await target.files?.item(0)?.text()
    if (blobData) {
        console.log("Data: ", blobData)

        const data = JSON.parse(blobData)
        console.log("Data: ", data.todos)

        setData(data.todos)

    }
}

export interface LoadDataProps {
    setData: (data: ToDo[]) => void
}

const LoadData = ({ setData }: LoadDataProps) => {
    return <div>
        Ingest:
        <input type="file" accept="text/json" onInput={(event) => fileHandler(event, setData)} />

    </div>
}

export default LoadData
