import React from 'react'
import useLocalStorageState from 'use-local-storage-state'
import { TodoStorage } from '../Constants'
import { DoNextData } from '../DoData'
import LoadData from '../LoadData'
import UnloadData from '../UnloadData'

export const defaultToDoData: DoNextData = {
    todos: [
        { text: "Download default configuration", done: 0 },
        { text: "Edit to make it yours", done: 0 },
        { text: "Ingest your data", done: 0 },
    ]
}

const Storage = () => {

    const [localStorage, setLocalStorage] = useLocalStorageState(TodoStorage, {
        defaultValue: defaultToDoData
    })

    const persistData = (data: DoNextData) => {
        // setData(data)
        setLocalStorage(data)
    }

    return <>
        <div className='todo-storage'>
            <LoadData setData={persistData} />
            <UnloadData data={localStorage} />
        </div>
        <div className='event-storage'>
            <LoadData setData={persistData} />
            <UnloadData data={localStorage} />
        </div>
    </>
}

export default Storage
