import React, { useState } from 'react'
// import { datestampToMinute } from '../DateUtilities'
import { Event } from "../DoData"

interface EventEditProps {
    event: Event
    save: (event: Event) => void
}

const EventEdit = ({ event, save }: EventEditProps) => {
    const [text, setText] = useState(event.text || '')
    // const beginning = project.beginning || datestampToMinute()

    return <div >
        <label>text:<input
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setText(data)
            }}
            value={text}
        /></label>
        <br />
        {/* <label>beginning:<input readOnly value={new Date(beginning).toISOString()} /></label> */}
        <button onClick={() =>
            save(
                { text: text, start: 0, duration: 0, }
            )
        } >Done</button>

    </div>
}

export default EventEdit
