import React, { useState } from 'react'
import { Event } from "../DoData"
import Entry from './Entry'

interface EventEditProps {
    event: Event
    save: (event: Event) => void
}

const EventEdit = ({ event, save }: EventEditProps) => {
    const [text, setText] = useState(event.text || '')
    const now = new Date()
    const [year, setYear] = useState(now.getFullYear())
    const [month, setMonth] = useState(now.getMonth() + 1)
    const [date, setDate] = useState(now.getDate())

    return <div >
        <label>text:<input
            value={text}
            size={40}
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setText(data)
            }}
        /></label>
        <br />
        <label>date:<input
            defaultValue={year}
            maxLength={4}
            size={4}
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setYear(parseInt(data))
            }}
        /></label>
        <label>-<input
            defaultValue={month}
            maxLength={2}
            size={2}
            onInput={event => {
                const data = (event.target as HTMLInputElement).value
                setMonth(parseInt(data))
            }}
        /></label>
        <Entry prompt={'-'} value={date} size={2} setter={setDate} />
        {/* <label>-<input defaultValue={now.getDate()} /></label> */}
        <br />
        <label>time:<input /></label>
        <label>:<input /></label>
        <br />
        <label>duration:<input defaultValue={'30'} /></label>
        <br />
        <button onClick={() => {
            const start = Date.parse(`${year}-${month}-${date}`)
            console.log(`Start date: ${new Date(start).toISOString()}`)
            save(
                { text: text, start: start, duration: 0, }
            )
        }
        } >Done</button>

    </div>
}

export default EventEdit
